tinymce.PluginManager.add("example", function(t, e) {
t.addButton("example", function() {
                var n = [],
                    r = "8pt 10pt 12pt 14pt 18pt 24pt 36pt",
                    i = r;
                return (i.split(" "), function(e) {
                    n.push({
                        text: e,
                        value: e
                    })
                }), {
                    type: "listbox",
                    text: "example",
                    tooltip: "example",
                    values: n,
                    fixedWidth: !0,
                    onPostRender: t(n, "example"),
                    onclick: function(t) {
                        t.control.settings.value && e.execCommand("FontSize", !1, t.control.settings.value)
                    }
                }
            })
});
